    private void jBtnPersonaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnPersonaActionPerformed
        Persona persona1 = new Persona("Juan", "juan777@juancito.com");
        jTxaPersona.setText(persona1.toString());
       jTxaPersona.append("\n");
       
       
        Persona persona2= new Persona();
        persona2.setNombre("Laura Castro");
        persona2.setEmail("LauraCastro@hotmail.com");
        jTxaPersona.append(persona2.toString());
        universidad.agregarPersona(persona2);
        jTxaUniversidad.setText(universidad.toString());
    }//GEN-LAST:event_jBtnPersonaActionPerformed

