    private void jBtnPotenciaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnPotenciaActionPerformed
     int base=Integer.parseInt(this.jTxtBase.getText());
             int exponente=Integer.parseInt(this.jTxtExponente.getText());
             double result=Recursividad.potencia(base, exponente);
             jTxtPotencia.setText(String.valueOf(result));
    }//GEN-LAST:event_jBtnPotenciaActionPerformed
