    private void jBtnSumaArregloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnSumaArregloActionPerformed
     
          jTxaSumaArreglo.setText("Contenido del arreglo:\n" + Arrays.toString(arreglo));
           if(this.jTxtPosicionArreglo.getText().isEmpty()){
              JOptionPane.showMessageDialog(this, "Favor ingresar un numero ");
              return;
          }  
            int n = Integer.parseInt(jTxtPosicionArreglo.getText());
          if(Integer.parseInt(this.jTxtPosicionArreglo.getText())>=this.arreglo.length+1 ){
              JOptionPane.showMessageDialog(this, "Favor ingresar un numero menor, este sobrepasa el tamanno del arreglo");
              return;
          }
        
         jTxaSumaArreglo.append("\nSuma hasta la posición " + n + " = "+ Recursividad.suma_arreglo(arreglo, n-1));
    }//GEN-LAST:event_jBtnSumaArregloActionPerformed
